package JavaCoding;

public class LoopingConcepts {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Looping Concepts in Java 
		//While loop 
		int i=1; //initiallisation 
		while(i<=10) //Condition 
		{System.out.println(i);
		i=i+1;// Incremental or Decremental 
		}
		//for loop
	for (int j=0;j<10;j++)
	{
		System.out.println(j);
	}
	System.out.println("*******************************");
	for (int k=10;k>=1;k--)
	{
		System.out.println(k);
	}}}
		// Disadvantage of While Loop 
		// It will generate infinite loop if not given incremental or decremental part 






